import React, { useState } from 'react';
import { TextInput, View, StyleSheet, TouchableOpacity, Text } from 'react-native';
import validate from './validate'

const SignUpScreen = ({ navigation }) => {
  const fields = [
    [
      {
        name: "firstName",
        label: "First Name",
        type: "input",
        inputProps: {
          autoCorrect: false
        }
      },
      {
        name: "lastName",
        label: "Last Name",
        type: "input",
        inputProps: {
          autoCorrect: false
        }
      }
    ],
    [
      {
        name: "email",
        label: "Email",
        type: "input",
        inputProps: {
          autoCorrect: false,
          autoCapitalize: "none",
          keyboardType: "email-address"
        }
      }
    ],
    [
      {
        name: "subject",
        placeholder: "Pick a topic of your interest",
        pickerItems: [
          {
            label: "React Native",
            value: 0
          },
          {
            label: "React Hooks",
            value: 1
          },
          {
            label: "React Navigation",
            value: 2
          },
          {
            label: "React News",
            value: 3
          }
        ],
        type: "picker"
      }
    ],
    [
      {
        name: "password",
        label: "Password",
        type: "input",
        inputProps: {
          secureTextEntry: true
        }
      }
    ],
    [
      {
        name: "signUpButton",
        label: "Sign Up",
        type: "button"
      }
    ],
    [
      {
        name: "resetButton",
        label: "Reset",
        type: "button"
      }
    ]
  ];

  const [user, setEmail, setPassword] = useState({email:'', password:''})
  console.log(user)
  return (
    <View style={styles.container}>
      <TextInput
        value={user.email}
        onChangeText={(email) => setEmail({ email })}
        placeholder={'Email'}
        style={styles.input}
      />

      <TextInput
        //value={this.state.password}
        //onChangeText={(password) => this.setState({ password })}
        placeholder={'Password'}
        secureTextEntry={true}
        style={styles.input}
      />

      <TextInput
        //value={this.state.password}
        //onChangeText={(password) => this.setState({ password })}
        placeholder={'Retype password'}
        secureTextEntry={true}
        style={styles.input}
      />

        <TouchableOpacity 
          //onPress={this.onLogin.bind(this)}
          style={styles.button}>
          <Text style={styles.inputText}>
            Create account
          </Text>
        </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#ecf0f1',
  },
  input: {
    width: 200,
    height: 44,
    padding: 10,
    borderWidth: 1,
    borderColor: 'black',
    marginBottom: 10,
  },
  inputText: {
    color:"white"
  },
  button:{
    width:"80%",
    backgroundColor:"#e34321",
    borderRadius:25,
    height:50,
    width: 140,
    alignItems:"center",
    justifyContent:"center",
    marginTop:40,
    marginBottom:10
  },
});

export default SignUpScreen;
