import React from 'react';
import { StyleSheet, View, TouchableOpacity, Text} from 'react-native';
import { TextInput } from 'react-native-paper';

const AboutUsScreen = () => {
	const [text, setText] = React.useState('');

  return (
		<View style={styles.container}>
			<TextInput
				style={styles.input}
				label="Email"
				value={text}
				underlineColor='#e34321'
				onChangeText={text => setText(text)}
    	/>

			<TextInput
				label="Email"
				value={text}
				underlineColor='#e34321'
				onChangeText={text => setText(text)}
    	/>



      <TouchableOpacity 
        //onPress={this.onLogin.bind(this)}
        style={styles.button}>
        <Text style={styles.inputText}>
          Create account
        </Text>
      </TouchableOpacity>
    </View>
	);
}

const styles = StyleSheet.create({
  container: {
		width: 600,
    // alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    width: 200,
    height: 44,
    padding: 10,
    marginBottom: 10,
  },
  inputText: {
    color:"white"
  },
  button:{
    width:"80%",
    backgroundColor:"#e34321",
    borderRadius:25,
    height:50,
    width: 140,
    alignItems:"center",
    justifyContent:"center",
    marginTop:40,
    marginBottom:10
  },
});

export default AboutUsScreen;
