import React from 'react';
import { StyleSheet, ImageBackground, View, TouchableOpacity, Text} from 'react-native';
import * as Font from 'expo-font';

const HomeScreen = ({ navigation }) => {
  const [loaded] = useFonts({
    Montserrat: require('./../../assets/fonts/Montserrat.ttf'),
  });

  if (!loaded) {
    return null;
  }

  return (
		<ImageBackground source={require('./../../assets/home.jpg')} style={styles.backgroundImage}>
			<View style={styles.fixToText}>

        <Text style={styles.defaultMessage}>
          Welcome to HotelHub! Start by signing into your account or creating a new one.
        </Text>

        <View style={styles.space} />

        <TouchableOpacity 
          //onPress={() => navigation.navigate('Signup')}
          style={styles.button}>
          <Text style={styles.inputText}>
            Sign up
          </Text>
        </TouchableOpacity>

				<TouchableOpacity 
          onPress={() => navigation.navigate('Login')}
          style={styles.button}>
          <Text style={styles.inputText}>
            Log in
          </Text>
        </TouchableOpacity>
    	</View>
		</ImageBackground>
	);
}

const styles = StyleSheet.create({
	backgroundImage: {
		flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
	},
	fixToText: {
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  inputText: {
    color:"white",
  },
  defaultMessage: {
    alignItems: 'center',
    color:"#274650",
    justifyContent: 'center',
    fontFamily: 'Montserrat',
  },
  button:{
    width:"80%",
    backgroundColor:"#1F3B3F",
    borderRadius:25,
    height:50,
    width: 300,
    alignItems:"center",
    justifyContent:"center",
    marginBottom:10
  },
  space: {
    width: 20,
    height: 50
  }
});

export default HomeScreen;
